import { link } from '../mixins/link';
import { VantComponent } from '../common/component';
VantComponent({
    classes: [
        'title-class',
        'label-class',
        'value-class',
        'right-icon-class',
        'hover-class',
    ],
    mixins: [link],
    props: {
        title: null,
        value: null,
        icon: String,
        size: String,
        label: String,
        center: Boolean,
        isLink: Boolean,
        required: Boolean,
        clickable: Boolean,
        titleWidth: String,
        customStyle: String,
        arrowDirection: String,
        useLabelSlot: Boolean,
        border: {
            type: Boolean,
            value: true,
        },
        titleStyle: String,
        iconSize: {
            type: Number,
            value: 22
        },
        iconSlot: {
            type: Boolean,
            value: false
        },
        showIcon: {
            type: Boolean,
            value: true
        },
        titleSlot: {
            type: Boolean,
            value: false,
        },
        valueSlot: {
            type: Boolean,
            value: false,
        },
        bodyFull: {
            type: Boolean,
            value: true,
        },
    },
    methods: {
        onClick(event) {
            this.$emit('click', event.detail);
            this.jumpLink();
        },
    },
});
